options.helpers:
setInterval(() => {
  const dashboardUID = window.location.pathname.split('/')[2];


  const targetLabels = [
    'Dne≈°n√° spotreba',
    'Dne≈°n√° cena',
    'Mesaƒçn√° spotreba',
    'Mesaƒçn√° cena',
    'Spotreba za vybran√© obdobie',
    'Cena za vybran√© obdobie',
  ];

  const allDivs = document.querySelectorAll('div');
  allDivs.forEach(div => {
    if (targetLabels.includes(div.innerText.trim())) {
      div.style.width = '100%';
      div.style.display = 'block';
    }
  });
}, 500);


// border radius 
setTimeout(() => {
  const dashboardUID = window.location.pathname.split('/')[2];


  const keywords = ['Pripojen√Ω', 'Chyba pripojenia', 'Virtu√°lny bod'];

  const observer = new MutationObserver(() => {
    document.querySelectorAll('div[style*="background: linear-gradient"]').forEach(el => {
      const text = el.innerText.trim();
      if (keywords.some(keyword => text.includes(keyword))) {
        el.style.borderRadius = '25px';
        el.style.overflow = 'hidden';
      }
    });
  });

  observer.observe(document.body, {
    childList: true,
    subtree: true,
  });
}, 300);

options.styles:
h1 {
  font-size: clamp(1.9rem, 2.5vw, 3rem);  
  font-family: 'Montserrat', sans-serif;
  font-weight: bold;
  margin: 0;
  white-space: normal;
  max-width: 100%;
}

.header-container {
  overflow: hidden;
  display: flex;
  flex-wrap: nowrap;          
  align-items: center;         
  justify-content: flex-start; 
  gap: 20px;                   
  padding-left: 10px;
  text-align: left;
}

.logo-desktop,
.logo-mobile {
  height: clamp(40px, 8vw, 80px);
  width: auto;
}

.logo-mobile {
  display: none;
}

@media (max-width: 576px) {
  .logo-desktop {
      display: none;
  }
  .logo-mobile {
      display: none;   
  }
 }

@media (min-width: 577px) and (max-width: 991px) {
  .logo-desktop {
      display: none;
  }
  .logo-mobile {
      display: block;
      height: clamp(40px, 8vw, 80px);
  }
}

@media (min-width: 992px) {
  .logo-desktop {
      display: block;
  }
  .logo-mobile {
      display: none;
  }
}
